var payload = JSON.stringify(msg.payload.object.payload);

let [device_eui, DHT11_pin, DHT11_status, temperature, humidity,
mag_pin_1, durations_high_1, durations_low_1, 
mag_pin_2, durations_high_2, durations_low_2, 
mag_pin_3, durations_high_3, durations_low_3] = 
payload.split(",")

var total_duration_z = parseFloat(durations_high_1) + parseFloat(durations_low_1);
var total_duration_y = parseFloat(durations_high_2) + parseFloat(durations_low_2);
var total_duration_x = parseFloat(durations_high_3) + parseFloat(durations_low_3);

var magnetic_field_z = (total_duration_z - 14.353)/0.118;
var magnetic_field_y = (total_duration_y - 14.353)/0.118;
var magnetic_field_x = (total_duration_x - 14.353)/0.118;
var total_vector_value = Math.sqrt(magnetic_field_z * magnetic_field_z + magnetic_field_y * magnetic_field_y + magnetic_field_x * magnetic_field_x);

msg.payload = {
        device_eui: device_eui,
        DHT11_pin: DHT11_pin,
        DHT11_status: DHT11_status,
        temperature: parseFloat(temperature),
        humidity: parseFloat(humidity),
        mag_pin_1: parseInt(mag_pin_1),
        durations_high_1: parseInt(durations_high_1),
        durations_low_1: parseInt(durations_low_1),
        mag_pin_2: parseInt(mag_pin_2),
        durations_high_2: parseInt(durations_high_2),
        durations_low_2: parseInt(durations_low_2),
        mag_pin_3: parseInt(mag_pin_3),
        durations_high_3: parseInt(durations_high_3),
        durations_low_3: parseInt(durations_low_3),
        magnetic_field_z: magnetic_field_z,
        magnetic_field_y: magnetic_field_y,
        magnetic_field_x: magnetic_field_x,
        total_vector_value: total_vector_value
};

return msg;
